module Problem6

let findMarker size stream =
    (stream
     |> Seq.mapi (fun i c -> i, c)
     |> Seq.windowed size
     |> Seq.skipWhile (fun m -> m |> Seq.map snd |> Set.ofSeq |> Set.count < size)
     |> Seq.head
     |> Seq.last
     |> fst)
    + 1



open Xunit

[<Theory>]
[<InlineData("bvwbjplbgvbhsrlpgdmjqwftvncz", 5)>]
[<InlineData("nppdvjthqldpwncqszvftbrmjlhg", 6)>]
[<InlineData("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10)>]
[<InlineData("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11)>]
let ``Finds -> start-of-packet`` (stream: string, expectedPosition: int) =
    let markerPosition = findMarker 4 stream
    Assert.Equal(expectedPosition, markerPosition)


[<Theory>]
[<InlineData("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 19)>]
[<InlineData("bvwbjplbgvbhsrlpgdmjqwftvncz", 23)>]
[<InlineData("nppdvjthqldpwncqszvftbrmjlhg", 23)>]
[<InlineData("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 29)>]
[<InlineData("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 26)>]
let ``Finds -> start-of-message`` (stream: string, expectedPosition: int) =
    let markerPosition = findMarker 14 stream
    Assert.Equal(expectedPosition, markerPosition)


let runProblem () =
    let input =
        """"""

    printfn "%A" (findMarker 4 input)
    printfn "%A" (findMarker 14 input)
